import { createStore } from "redux";
import rootReducer from "./reducers";
import { createLogicMiddleware } from 'redux-logic';
import rootLogic from './logic/';
import axios from 'axios';

// The store is where application state is held.
// The store also allows state to be updated when users
// dispatch actions.

// Injected dependencies for logic
const deps = {
    httpClient: axios
};

// Create middleware
const logicMiddleware = createLogicMiddleware(rootLogic, deps);

// Add middleware to our store
export default createStore(rootReducer,logicMiddleware);

// If we were using without redux-logic:
// export default createStore(rootReducer);
